function FTMIsEnabled(key){
    var response = false;
    if(typeof featureToggles[key] != 'undefined' && typeof featureToggles[key]['enabled'] != 'undefined' && featureToggles[key]['enabled'] == true){
        response = true;
    }
    return response;
}

