    // Function to add users to subscribe user and redirect to shop to buy subscription
    function buySubsctiptionProcess() {
        $.ajax({
            url: base_url + "subscribeUser",
            method: "post",
            beforeSend: function() {
                blockUI(".modal-content");
            },
            data: $(".add_user_subscribe").find("*").serialize(),
            success: function(data) {
                unBlockUI('.modal-content');
                if (data != 'false') {
                    $.ajax({
                        url: base_url + "getOdoo",
                        type: 'post',
                        beforeSend: function() {
                            blockUI(".modal-content");
                        },
                        data: {
                            'username': data.userData.username,
                            'password': data.userData.password,
                            'tenantId': data.tenantId,
                            'subscription_type': $("[name='subscriptionType']:checked").val(),
                            'subscriptions': data.subscription
                        },
                        success: function(data) {
                            unBlockUI('.modal-content');
                            if (data.type == 'success')
                                window.location.href = data.redirectUrl;
                            else
                                notify("error", data.message);
                        }
                    });
                }
            }
        });
    }

    /*
     Used specially for vue JS in qualification to support
     button hide show based on view
     */
    function resetVueMode(mode) {
        if(window.rootData) {
            window.rootData.currentTab.mode = mode;
            if(mode == "view")
                $(".close-btn").css("display","inline-block");
        }
    }

