    // Reset oprational button on each tabe change
    function resetOprationalButtons(targ) {
        // hide save / show edit
        // check that is current status is in detail or edit mode
        console.log("inside resetOprationalButtons",targ)
        var editModeWrapper = $($(targ).find(targ + "_form"));
        if (editModeWrapper.css('display') == 'block') {
            $(".edit-btn, .delete-btn").hide();
            $(".save-btn, .close-btn").show();
        } else {
            if (targ == "#tab_activity" || targ == "#tab_users" || targ == "#tab_document" || targ == "#tab_address" || targ == "#overview" || targ == "#tab_note" || targ == "#tab_room" || targ == "#tab_gallary" || targ == "#tab_things_to_do" || targ == "#tab_hrdactivity" || targ == "#tab_schedule" || targ == "#tab_Email_report_attachment" || targ == "#tab_Email_attachment" || targ == "#tab_change_passwords" || targ == "#tab_reports" || targ == "#tab_log_detail" || targ=="#tab_qualification"|| targ=="#person_details" || targ=="#tab_membership") {
                $(".edit-btn, .save-btn, .delete-btn, .close-btn,.report-btn").hide();
            } else if (targ == "#tab_detail_parameter") {
                $(".edit-btn,.delete-btn,.close-btn").hide();
                $(".save-btn").show();
            } else if (targ == "#tab_seminar_budget") {
                $(".edit-btn, .save-btn, .delete-btn, .close-btn,.report-btn").hide();
            }
            else {
                // check that if user has address tab edit button
                if (targ == "#tab_activity") {
                    if ($(".edit-btn").attr('addressEditPermission')) {
                        $(".edit-btn").show();
                    }
                    if ($(".edit-btn").attr('addressDeletePermission')) {
                        $(".delete-btn").show();
                    }
                    $(".close-btn").hide();
                } else {
                    $(".edit-btn, .delete-btn,.report-btn,.resend_password").show();
                    $(".save-btn, .close-btn").hide();
                }
            }
        }

        $(".delete-btn").attr('id', targ.replace('#', '') + "_delete_btn");
        $(".save-btn").attr('id', targ.replace('#', '') + "_save_btn");
        $(".edit-btn").attr('id', targ.replace('#', '') + "_edit_btn");
        if (targ == '#tab_bank') {
            console.log("Innn tabb bankk");
            $(".edit-btn, .delete-btn").attr("id", "").hide();
            $(".bank-edit-btn, .bank-delete-btn").show();
            $(".bank-edit-btn").attr('data-original-title', TooltipEditBankDetails);
            $(".bank-delete-btn").attr('data-original-title', TooltipDeleteBankDetails);
        } else if (targ == '#tab_Email_content') {
            $(".edit-btn").attr('data-original-title', TooltipEditTemplateContent);
        } else if (targ == '#tab_description') {
            $(".edit-btn").attr('data-original-title', seminarDescriptionEdit);
            $(".delete-btn").attr('data-original-title', seminarDescriptionDelete);
            $(".report-btn").hide();
        } else {
            var tooltipEdit = $(".right-content").attr('id');
            if (typeof tooltipEdit != "undefined") {
                $(".edit-btn").attr('data-original-title', tabToolTipMessageEdit[tooltipEdit.replace('-', '_')]);
                $(".delete-btn").attr('data-original-title', tabToolTipMessageDelete[tooltipEdit.replace('-', '_')]);
                $(".bank-edit-btn, .bank-delete-btn").hide();
            }
        }

          if($("#campaign_detail").attr('status')=='2'){
                console.log("inside here");
                 $(".edit-btn").hide();
            }
        $(".close-btn").attr('currentTab', targ);
        if (targ == "#tab_attendance") {

            $(".attendance-save-btn").css("display", "inline-block");
        }else{
            $(".attendance-save-btn").css("display", "none");
        }
    }

    function resetButtonsIdAfterClose() {
        var targ = $(".nav-tabs li.active a").attr("data-target");
        if (typeof(targ) != "undefined"){
            $(".delete-btn").attr('id', targ.replace('#', '') + "_delete_btn");
            $(".save-btn").attr('id', targ.replace('#', '') + "_save_btn");
            $(".edit-btn").attr('id', targ.replace('#', '') + "_edit_btn");
            $(".close-btn").attr('currentTab', targ);
        }
    }

    function setViewByMode(targ) {
        if ($(targ + "_info").css('display') == 'block') {
            console.log("In iff");
            if (targ == "#tab_detail") {
                // check if detail tab then dont slide info view
                $(targ + "_form").slideToggle("slow", function () {
                    $(targ + "_info").hide();
                });
            } else {
                $(targ + "_info").slideToggle("slow", function () {
                    $(targ + "_form").slideToggle("slow");
                });
            }
            $(".edit-btn, .delete-btn,.restore-btn, .disable-btn,.report-btn,.resend_password,.event-filter,.send_mail_to_event_attendees,.send-mail-seminar-registration").hide();
            if (targ == "#tab_bank")
                $(".bank-edit-btn, .bank-delete-btn").hide();
            $(".save-btn, .close-btn").show();
            $('#tab_detail_add_btn').hide();

            showElementIntoTranslationMode();

        } else {
            console.log("In else");
            if (targ == "#tab_detail") {
                // check if detail tab then dont slide info view

                $(targ + "_form").slideToggle("slow", function () {
                    $(targ + "_info").show();
                });
            } else {
                $(targ + "_form").slideToggle("slow", function () {
                    $(targ + "_info").slideToggle("slow");
                });
            }

            // check that if there is not a single row on left side
            if ($(targ + "_info").length > 0) {
                $(".edit-btn, .delete-btn,.report-btn,.resend_password").show();
                if (targ == "#tab_bank")
                    $(".bank-edit-btn, .bank-delete-btn").show();
                $(".save-btn, .close-btn").hide();
                $('#tab_detail_add_btn').show();
                setViewByModeSoftDelete();
            } else {
                $(".edit-btn,.disable-btn,.restore-btn, .delete-btn,report-btn,.resend_password,.event-filter,.send_mail_to_event_attendees,.send-mail-seminar-registration").hide();
                $(".save-btn, .close-btn").show();
                $('#tab_detail_add_btn').show();
            }

            if(targ=="#tab_activity" || targ=="#tab_address"){
                $(".edit-btn, .delete-btn,.report-btn,.resend_password,#tab_detail_resend_password").hide();
            }
            if (targ == "#tab_reports") {
                $(".edit-btn, .delete-btn,.report-btn").hide();
            }
            showElementIntoTranslationMode();
        }
        changeTabOrderOfForm();

    }

function changeTabOrderOfForm(){
    setTimeout(() => {
        $('.right-content input,select,.right-content .select2-input').each(function(i) {
            console.log($(this).attr('noautotagindex'), "tabind");
            if ( this.type != 'hidden' && ($(this).attr('noautotagindex') === 'undefined' || !$(this).attr('noautotagindex'))) {
                var $input = $(this);
                $input.attr("tabindex", i+1);
            }
        });

        console.log($(".select2-input").length, "Tests");
    }, 500);
}
